import React, { useState } from "react";
import VoiceRecorder from "./VoiceRecorder.tsx";

const Main: React.FC = () => {
  const [userText, setUserText] = useState("");
  const [botText, setBotText] = useState("");
  const [botAudioUrl, setBotAudioUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [language, setLanguage] = useState("en"); // Default English

  const handleRecordingComplete = async (audioBlob: Blob) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("audio", audioBlob, "recording.webm");
      formData.append("language", language);

      const res = await fetch("/api/voice-chat", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (res.ok) {
        setUserText(data.user_text);
        setBotText(data.bot_text_local);
        setBotAudioUrl(data.bot_audio_url);
      } else {
        alert(data.error || "Error from backend");
      }
    } catch (err) {
      alert("Request failed");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Sarvam Voice Chat MVP</h1>
      <label>
        Choose Language:
        <select value={language} onChange={e => setLanguage(e.target.value)} style={{ marginLeft: 8 }}>
          <option value="en">English</option>
          <option value="hi">Hindi</option>
          <option value="ml">Malayalam</option>
          {/* Add more Sarvam supported languages here */}
        </select>
      </label>
      <br />
      <VoiceRecorder onRecordingComplete={handleRecordingComplete} />
      {loading && <p>Processing...</p>}
      <h3>User said:</h3>
      <p>{userText}</p>
      <h3>Bot said:</h3>
      <p>{botText}</p>
      {botAudioUrl && (
        <audio src={botAudioUrl} controls autoPlay style={{ marginTop: 10 }} />
      )}
    </div>
  );
};

export default Main;
